import java.util.stream.Collectors;

public class Main {
  public static void main(String[] args) {
    // Completa la función decodificarROT13
    System.out.println(decodificarROT13("Guvf vf n frperg zrffntr!")); // Debería mostrar 'This is a secret message!'
    System.out.println(decodificarROT13("Uryyb Jbeyq!")); // Debería mostrar 'Hello World!'
  }

  public static String decodificarROT13(String mensaje) {
    return mensaje.chars()
      .mapToObj(c -> Character.isLetter(c) ? (char) (c + (Character.toLowerCase(c) < 'n' ? 13 : -13)) : (char) c)
      .map(String::valueOf)
      .collect(Collectors.joining());
  }
}

